    reader.onload = function(e) {
    // Use reader.result
    alert(reader.result)
    }
  reader.readAsText(files[0],'gbk');
  this.setState({text:reader.result});
}
  render() {
    return (
      <div>
        <ReactFileReader handleFiles={this.handleFiles} fileTypes={'.csv'}>
          <button className='btn'>Upload</button>
        </ReactFileReader>
          <button onClick={console.log(this.state)}>b1</button>
      </div>
    );
  }
}
export default Home;
