import React from "react"
import ReactFileReader from 'react-file-reader';
class Home extends React.Component {
  constructor(props) {
    super(props); // You have to run this before you init state
    this.state = {};
  }
  handleFiles = files => {
    var reader = new FileReader();
    reader.onload = (e) => {
    // Use reader.result
    alert(reader.result)
    this.setState({text:this.csvToTable(reader.result)});
    }
  reader.readAsText(files[0],'gbk');
}
csvToTable = (content) => {
// 对csv文件的数据先以行分割
  var userList = content.split("\n");
  var data = [];
// 我们在对每一行以逗号作分割
  for (var i = 0; i < userList.length; i++) {
      var userary = userList[i].split(",");
      var tr = [];
  // 对每行的内容遍历到td标签去
      for (var j = 0; j < userary.length; j++) {
        tr.push(userary[j]);
      }
      data.push(tr);
  }
  return data;
}
  print = () => {
    console.log(this.state);
  }
  render() {
    return (
      <div>
        <ReactFileReader handleFiles={this.handleFiles} fileTypes={'.csv'}>
          <button className='btn'>Upload</button>
        </ReactFileReader>
          <button onClick={this.print}>b1</button>
      </div>
    );
  }
}
export default Home;
