import React from "react"
import ReactFileReader from 'react-file-reader';
class Pic extends React.Component {
  // componentDidMount = () => {

  // }
  handleFiles = files => {
    var reader = new FileReader();
    reader.onload = (e) => {
    // Use reader.result
      this.setState({text:this.csvToTable(reader.result)});
    }
    reader.readAsText(files.files[0],'gbk');
  }
  csvToTable = (content) => {//csv转表格obj格式
    var userList = content.split("\n");
    var data = [];
    for (var i = 1; i < userList.length; i++) {
      var userary = userList[i].split(",");
      var tr = {};
      //表里的7行
      tr.id = userary[0]
      tr.platform = userary[1];
      tr.key = userary[2];
      tr.shop = userary[3];
      tr.name = userary[4];
      tr.price = userary[5];
      tr.linkName = userary[4];
      //缓存链接
      tr.link = userary[6];
      data.push({...tr});
    }
    return data;
  }
  render() {
    return (
      <div>
        <ReactFileReader handleFiles={this.handleFiles} fileTypes={'.csv'}>
          <button className='btn'>Upload</button>
        </ReactFileReader>
        Page1
      </div>
    );
  }
}
export default Pic;
